import React, { useState } from 'react';
import img1 from '../assets/img1.png';


function Home() {
  const [isFlipped, setIsFlipped] = useState(false);

  const handleFlip = (direction) => {
    if (direction === 'left') {
      setIsFlipped(true);
    } else if (direction === 'right') {
      setIsFlipped(false);
    }
  };

  return (
    <div className="d-flex justify-content-center" style={{ marginTop: "300px", marginBottom: "250px" }}>
      <div className="flip-image-container">
        <h1 className="title">Do you like Marshmellow?</h1>
        <div className={`image-container ${isFlipped ? 'flipped' : ''}`}>
          <div className="image-front">
            <img src={img1} className="img-fluid w-75" alt="Front Image" />
          </div>
          <div className="image-back">
            <img src="image2.jpg" alt="Back Image" />
          </div>
        </div>
        <div className="buttons-container">
          <button onClick={() => handleFlip('left')}>Flip Left</button>
          <button onClick={() => handleFlip('right')}>Flip Right</button>
        </div>
      </div>
    </div>
  );
}

export default Home;
