import React, { useState } from 'react';
import img1 from '../assets/img1.png';
import img2 from '../assets/img2.png';
import img3 from '../assets/img3.png';
import img4 from '../assets/img4.png';
import img5 from '../assets/img5.png';
import img6 from '../assets/img6.png';

function Home() {
  const [isFlipped, setIsFlipped] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);

  const imgSequenceLeft = [img4, img5, img6];
  const imgSequenceRight = [img1, img2, img3];

  const handleFlip = (direction) => {
    setIsFlipped(!isFlipped);

    if (direction === 'left') {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % imgSequenceLeft.length);
    } else if (direction === 'right') {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % imgSequenceRight.length);
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="flip-image-container text-center">
        <h1 className="title mb-4">Do you like Marshmallow?</h1>
        <div className={`image-container ${isFlipped ? 'flipped' : ''}`}>
          <div className="image-front">
            <img src={isFlipped ? imgSequenceLeft[currentIndex] : img1} className="img-fluid w-75" alt="Front Image" />
          </div>
          <div className="image-back">
            <img src={isFlipped ? imgSequenceRight[currentIndex] : img2} className="img-fluid w-75" alt="Back Image" />
          </div>
        </div>
        <div className="buttons-container mt-4">
          <button className="btn btn-primary mr-2" onClick={() => handleFlip('left')}>
            Flip Left
          </button>
          <button className="btn btn-primary" onClick={() => handleFlip('right')}>
            Flip Right
          </button>
        </div>
      </div>
    </div>
  );
}

export default Home;
