import React, { useState } from 'react';
import img1 from '../assets/img1.png';
import img2 from '../assets/img2.png';
import img3 from '../assets/img3.png';
import image1 from '../assets/image1.png';
import image2 from '../assets/image2.png';
import image3 from '../assets/image3.png';

function Home() {
  const [counterLeft, setCounterLeft] = useState(0);
  const [counterRight, setCounterRight] = useState(0);

  // const goodReaction = [img1, img2, img3];
  const goodReaction = [image1, image2, image3];

  // const handleFlip = (direction) => {
  //   if (direction === 'left') {
  //     setCounterLeft((prevCounter) => (prevCounter + 1) % bedReaction.length);
  //   } else if (direction === 'right') {
  //     setCounterRight((prevCounter) => (prevCounter + 1) % goodReaction.length);
  //   }
  // };

  const handleFlip = () => {
    setCounterLeft((prevCounter) => (prevCounter + 1) % goodReaction.length);
  };
  
  

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="flip-image-container text-center">
        <h1 className="title mb-4">Do you like Marshmallow?</h1>
        <div className={`image-container ${'flipped'}`}>
          <div className="image-front">
            <img src={goodReaction[counterLeft]} className="img-fluid w-75" alt="Front Image" />
          </div>
          {/* <div className="image-back">
            <img src={goodReaction[counterRight]} className="img-fluid w-75" alt="Back Image" />
          </div> */}
        </div>
        <div className="buttons-container mt-4">
          <button className="btn btn-primary mr-2" id="leftButton" onClick={handleFlip}>
            YES
          </button>
          {/* <button className="btn btn-primary" id="rightButton" onClick={() => handleFlip('right')}>
            NO
          </button> */}
        </div>
        <p>Counter (Left): {counterLeft}</p>
        {/* <p>Counter (Right): {counterRight}</p> */}
      </div>
    </div>
  );
}

export default Home;
